import cv2import numpy as np# 打开视频文件cap = cv2.VideoCapture('/Users/johnshreen/.spyder-py3/data/2024-07-18_2Rafts_timeStep0.0001_total1.0s/Simulation_RK45_2Rafts_200rps_B0.01T_m1e-08_startPosMeth2_timeStep0.0001_1.0s.mp4')# 设置要读取的帧数frame_number = 8000cap.set(cv2.CAP_PROP_POS_FRAMES, frame_number)# 读取一帧ret, frame = cap.read()if ret:    # 显示视频帧    cv2.imshow('Frame', frame)    # 鼠标点击事件回调函数    points = []    def click_event(event, x, y, flags, params):        if event == cv2.EVENT_LBUTTONDOWN:            points.append((x, y))            if len(points) == 2:                # 计算像素距离                distance = np.linalg.norm(np.array(points[0]) - np.array(points[1]))                print(f'Pixel Distance: {distance}')                # 画出选定的点和线                cv2.circle(frame, points[0], 5, (0, 255, 0), -1)                cv2.circle(frame, points[1], 5, (0, 255, 0), -1)                cv2.line(frame, points[0], points[1], (255, 0, 0), 2)                cv2.imshow('Frame', frame)    # 设置鼠标回调    cv2.setMouseCallback('Frame', click_event)    # 等待按键退出    cv2.waitKey(0)    # 释放视频对象cap.release()cv2.destroyAllWindows()